function transform(input) {

  try {
		var inputs = {};
		inputs['sessionid'] = input.SessionName; // String 
		gs.info("sessionmonkey: "+ input.SessionName);
		// Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
		// sn_fd.FlowAPI.getRunner().subflow('x_snc_woltersklu_0.complete_wkw_flow').inBackground().withInputs(inputs).run();

		// Execute Synchronously: Run in foreground. Code snippet has access to outputs.
		var result = sn_fd.FlowAPI.getRunner().subflow('x_snc_woltersklu_0.complete_wkw_flow').inForeground().withInputs(inputs).run();
		var outputs = result.getOutputs();
		return outputs;
		// Current subflow has no outputs defined.		
	} catch (ex) {
		var message = ex.getMessage();
		gs.error(message);
	}

}
