sys_ux_data_broker_transform.script:
function transform(input) {

    try {
        var inputs = {};
        inputs['product'] = input.ProductName; // Array.Integer 
        inputs['organzation'] = input.OrganizationName; // String 
        inputs['package'] = input.PackageName; // String
        gs.info("flag:" + JSON.stringify(input));
        // Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
        // sn_fd.FlowAPI.getRunner().subflow('x_snc_woltersklu_0.update_and_generate_document_page').inBackground().withInputs(inputs).run();

        // Execute Synchronously: Run in foreground. Code snippet has access to outputs.
        var result = sn_fd.FlowAPI.getRunner().subflow('x_snc_woltersklu_0.update_and_generate_document_page').inForeground().withInputs(inputs).run();
        var outputs = result.getOutputs();

        // Get Outputs:
        // Note: outputs can only be retrieved when executing synchronously.
        var sessionid = outputs['sessionid']; // String
        var swt = outputs['swt']; // String
        var url = outputs['url']; // String

        var f_result = {
            "sessionid": sessionid,
            "swt": swt,
            "url": url + "Documents/Selection/?sessionid=" + sessionid,
            "org": input.OrganizationId,
            "prod": input.ProductName
        };
        gs.info('full_object' + JSON.stringify(f_result));
        return f_result;

    } catch (ex) {
        var message = ex.getMessage();
        gs.error(message);
    }

}
